#!/bin/bash
## Manage all output trees generated by a buildroot-based build.
## 
## Arguments:
##
##     --clean-only 
##     
##     [ --build ] [ --clean-first ] xctc_defconfig_fbn main_defconfig_fbn
## 
## Typical uses:
##
##     buildroot.all-output-trees.sh my_team_product_{xctc,main}_defconfig
##     
##     buildroot.all-output-trees.sh --build my_team_product_{xctc,main}_defconfig
##     
##     buildroot.all-output-trees.sh --clean-first my_team_product_{xctc,main}_defconfig
##     
##     buildroot.all-output-trees.sh --clean-only
## 

source "$(dirname "${BASH_SOURCE:?}")"/../../libexec/buildroot_cli.prolog.sh

source buildroot_api_all_output_trees.functions.sh

##

function main() { # ...

	xx_buildroot_all_output_trees "$@"
}

! [ "$0" = "${BASH_SOURCE}" ] || main "$@"
