#!/bin/bash
## Manage the host tree generated by a buildroot-based build.
## 
## Arguments:
##
##     --clean-only 
##     
##     [ --build ] [ --clean-first ]
## 
## Typical uses:
##
##     buildroot.host-tree.sh
##     buildroot.host-tree.sh --build
##     
##     buildroot.host-tree.sh --clean-only
##     buildroot.host-tree.sh --clean-first
## 

source "$(dirname "${BASH_SOURCE:?}")"/../../libexec/buildroot_cli.prolog.sh

source buildroot_api_host_tree.functions.sh

##

function main() { # ...

	load_buildroot_config

	xx_buildroot_host_tree "$@"
}

! [ "$0" = "${BASH_SOURCE}" ] || main "$@"
