#!/bin/bash
## Manage the target tree generated by a buildroot-based build.
## 
## Arguments:
##
##     --clean-only 
##     
##     [ --build ] [ --clean-first ]
## 
## Typical uses:
##
##     buildroot.target-tree.sh
##     buildroot.target-tree.sh --build
##     
##     buildroot.target-tree.sh --clean-only
##     buildroot.target-tree.sh --clean-first
## 

source "$(dirname "${BASH_SOURCE:?}")"/buildroot-scripts.prolog.sh

source buildroot_target_tree.functions.sh

##

function main() { # ...

	load_buildroot_config

	xx_buildroot_target_tree "$@"
}

! [ "$0" = "${BASH_SOURCE}" ] || main "$@"
